function pageprocessorPopNotification(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  if (window.device && !$rootScope.loggedIn) {
    navigator.notification.alert('Please login to access this feature.', null, 'Authentication Required', 'Ok');
    $location.path("/app/SignIn279/SignIn279");
    return;
  }
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  var logger = console.log;

  /*AK's magic*/
  var e = document.getElementsByClassName("app")[0];
  var $injector = angular.element(e).injector();
  var $q = $injector.get('$q');
  /* $rootScope.notificationcentre.badge = 1; */
  $scope.goToRedirectionLink = function(notification) {
    if (notification.redirectionUrl && notification.redirectionUrl != "Not Found") {
      if (window.device) {
        var href = notification.redirectionUrl
      } else {
        var href = notification.redirectionUrl
      }

      $location.path(href);
    }
  }

  $scope.getHeaders = function(url, method) {
    var headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer ' + $rootScope.user.accessToken,
      'X-TENANT-ID': $rootScope.user.tenant,
      'X-TENANT-DOMAIN': $rootScope.user.tenantdomain,
      'principal-user': $rootScope.username
    }

    if (!({
        GET: true,
        HEAD: true,
        OPTIONS: true
      })[method.toUpperCase()]) {
      headers['Content-Type'] = 'application/json';
    }

    if ($rootScope.site && $rootScope.site._id) headers['site-id'] = $rootScope.site._id;

    if ($rootScope.user.email && url.indexOf("/unifyed-notificationcentre/") != -1)
      headers['X-USER-EMAIL'] = $rootScope.user.email;

    return headers;
  }

  /***************************************************************************/
  //  Added callAPI as a wrapper to call authenticated microservice APIs
  /***************************************************************************/
  $scope.callAPI = function(url, method, data, callback) {
    try {
      if (!$rootScope.user.accessToken && url.indexOf("/open/") == -1) {
        console.log("User doesn't a token, this should be a public User.");
        $.unblockUI();
        return callback();
      }
      if (!$rootScope.isblocking) $.blockUI();
      var apiEndPoint = $rootScope.GatewayUrl + url;
      /*if(url.indexOf("/unifyedrbac") == 0) {
          apiEndPoint = "http://localhost:8085" + url.substring(12) 
      }*/

      var req = {
        method: method,
        url: apiEndPoint,
        headers: $scope.getHeaders(url, method),
        data: data,
        json: true
      };

      $http(req).then(function successCallback(res) {
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }
        return callback(res);
      }, function errorCallback(err) {
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }
        //return callback();
        /* Commented the code below since token refersh is not working */
        if (err.status == 404) {
          return callback();
        } else if (apiEndPoint.indexOf("/open/") != -1) {
          return callback();
        } else if (err.status == 0 || err.status == 401) {
          console.log("in callAPI, URL : " + apiEndPoint + " : error status=" + err.status);
          $rootScope.refreshToken(url, method, data, callback);
          return callback();
        }
      });
    } catch (e) {
      console.log(e);
      if (!$rootScope.isblocking) {
        $.unblockUI();
      }
      return callback();
    }
  };

  var NotificationCentre = function() {
    this.baseUrl = '/unifyed-notificationcentre/v1/';
  };
  NotificationCentre.prototype.get = function(type, skip, limit) {
    type = type || 'all';
    skip = skip || 0;
    limit = limit || 1;
    logger('**NotificationCentre-->get**');
    var defer = $q.defer();
    var uri = this.baseUrl + 'notifications?type=' + type + '&skip=' + skip + '&limit=' + limit;
    $scope.callAPI(uri, 'GET', null, function(response) {
      logger(uri, 'GET', null, response);
      defer.resolve(response);
    });
    return defer.promise;
  };

  NotificationCentre.prototype.count = function() {
    logger('**NotificationCentre-->head**');
    var uri = $rootScope.GatewayUrl + this.baseUrl + 'notifications?type=sent';
    var defer = $q.defer();
    $http.head(uri, {
      headers: $scope.getHeaders(uri, 'HEAD')
    }).then(function(response) {
      logger(uri, 'HEAD', null, response);
      defer.resolve(response);
    }, function(err) {
      defer.resolve({
        data: {
          status: false,
          msg: "Something went wrong"
        }
      });
    });
    return defer.promise;
  };

  NotificationCentre.prototype.mark = function(id) {
    logger('**NotificationCentre-->patch**');
    var uri = this.baseUrl + 'notification/' + id;
    var defer = $q.defer();
    $scope.callAPI(uri, 'PATCH', {}, function(response) {
      logger(uri, 'PATCH', {}, response);
      defer.resolve(response);
    });
    return defer.promise;
  };

  NotificationCentre.prototype.markAll = function(id) {
    logger('**NotificationCentre-->patch**');
    var uri = this.baseUrl + 'notifications';
    var defer = $q.defer();
    $scope.callAPI(uri, 'PATCH', {}, function(response) {
      logger(uri, 'PATCH', {}, response);
      defer.resolve(response);
    });
    return defer.promise;
  };

  NotificationCentre.prototype.remove = function(id) {
    logger('**NotificationCentre-->delete**');
    var uri = this.baseUrl + 'notification/' + id;
    var defer = $q.defer();
    $scope.callAPI(uri, 'DELETE', {}, function(response) {
      logger(uri, 'DELETE', {}, response);
      defer.resolve(response);
    });
    return defer.promise;
  };

  $scope.clear = function() {
    $scope.notifications = [];
    $scope.page = 0;
    $scope.limit = 1;
    $scope.notificationLoder = true;
    $scope.noMoreNotifications = false;
    $scope.loadClicked = false;
  }

  $scope.addToList = function(data) {
    for (var i = 0; i < data.length; i++) {
      if (data[i].status == 2) {
        document.getElementsByClassName("ng-isolate-scope")[2].style.display = "block";
        $(".ng-isolate-scope:nth-child(2)").show();
      } else {
        document.getElementsByClassName("ng-isolate-scope")[2].style.display = "none";
        $(".ng-isolate-scope:nth-child(2)").hide();
      }
      data[i].shortDescription = data[i].message ? data[i].message.slice(0, 150) : "No Message";
      //data[i].shortDescription = data[i].message.slice(0, 150);
      data[i]['show'] = "more";
      $scope.notifications.push(data[i]);
    }
  }

  var nc = new NotificationCentre();
  $scope.init = function() {
    $scope.clear();
    $scope.notificationLoder = true;
    $.blockUI();
    nc.get().then(function(response) {
      $scope.addToList(response.data.data);
      $scope.noMoreNotifications = true;
      // $scope.markAll();
      $scope.notificationLoder = false;
      $.unblockUI();
      $scope.page++;
    });
  };
  $scope.init();
  $rootScope.removeIndex = '';
  $rootScope.removeId = '';
  if (window.device) {
    console.log("Device");
    $scope.confirmDelete = function(btn) {
      console.log("button" + btn)
      if (btn == 1) {
        nc.remove($rootScope.removeId).then(function(response) {
          $scope.notifications.splice($rootScope.removeIndex, 1);
          $('#removeNotification').modal('hide');
          $scope.successmessage = 'Notification Deleted Successfully';
          setTimeout(function() {
            $scope.successmessage = null;
            $scope.$apply();
          }, 3000);
          if (!$scope.notifications.length) {
            $scope.init();
          }
        });

      } else {
        //$location.path("/home");
      }
      $scope.$apply();
    }

    $scope.removeModal = function(id, index) {
      console.log("MobileRemoval Call");
      $rootScope.removeIndex = index;
      $rootScope.removeId = id;
      document.getElementsByClassName('ng-isolate-scope')[2].style.display = 'none';
      document.getELementsByClassName("modal-backdrop").remove();
      navigator.notification.confirm("Are you sure you want to delete?", $scope.confirmDelete, 'Delete', ['Yes', 'No']);

    };

  } else {
    console.log("Other")
    $scope.removeModal = function(id, index) {
      $scope.removeIndex = index;
      $scope.removeId = id;
      $('#removeNotification').modal('show');
    };

  }
  // $scope.removeModal = function(id, index) {
  //   $scope.removeIndex = index;
  //   $scope.removeId = id;
  //   $('#removeNotification').modal('show');
  // };


  $scope.remove = function(id, index) {
    $scope.successmessage = null;
    nc.remove(id).then(function(response) {
      $scope.notifications.splice(index, 1);
      $('#removeNotification').modal('hide');
      $(".modal-backdrop.in").hide();
      $rootScope.notificationcentre.badge = 0;
      $scope.successmessage = 'Notification Dismissed Successfully';
      setTimeout(function() {
        $scope.successmessage = null;
        $scope.$apply();
      }, 3000);
      if (!$scope.notifications.length) {
        $scope.init();
      }
    });
  }
  $scope.mark = function(id, index) {
    nc.mark(id).then(function(response) {
      $scope.notifications[index].status = 3;
      $("#homePop").modal('hide');
    });
  }
  $scope.markAll = function() {
    nc.markAll().then(function(response) {
      $scope.notifications.forEach(function(notification, idx) {
        $scope.notifications[idx].status = 3;
      });
      $rootScope.notificationcentre.badge = 0;
    });
  }
  $scope.viewMoreData = function(i) {
    $scope.notifications[i].show = "less";
    $scope.notifications[i].status = 3;
  }
  $scope.viewLessData = function(i) {
    $scope.notifications[i]['show'] = "more";
  }
  $scope.loadMore = function() {
    $scope.loadClicked = true;
    nc.get(null, $scope.page * $scope.limit, $scope.limit).then(function(response) {
      $scope.loadClicked = false;
      $scope.noMoreNotifications = false;
      $scope.addToList(response.data.data);
      if (response.data.data.length != $scope.limit) {
        $scope.noMoreNotifications = true;
      }
      $scope.page++;
    });
  }

  $('.u8PopupBg').on('show.bs.modal', function(e) {
    setTimeout(function() {
      $(".modal-backdrop.in").hide();
    }, 300);
  });
  //fix modal popup issue
  var windowWidth = $(window).width();
  setTimeout(function() {
    $("#homePop").modal('show');
    if (windowWidth < 768) {
      $('.u8PopupBg').on('show.bs.modal', function(e) {
        $('.owl-stage-outer').addClass('remove-carousel');
        $('.owl-stage').addClass('remove-carousel');
      });

      $('.u8PopupBg').on('hidden.bs.modal', function(e) {
        $('.owl-stage-outer').removeClass('remove-carousel');
        $('.owl-stage').removeClass('remove-carousel');
      });
    }
  }, 1000);
}

