  $scope.selectSemester = function() {
    $scope.events = [];
    $scope.classes = [];
    $scope.mapLocations = [];
    $scope.exec1Count = 0;
    $scope.exec2Count = 0;
    for (var i = 0; i < 7; i++) {
      $scope.weekdata[i].data = [];
    }
    $scope.weekataglance = [];
    var date = new Date();
    var d = date.getDate();
    var m = date.getMonth();
    var y = date.getFullYear();
    var getBaseEventObj = function(currentclass) {
      var event = {
        title: currentclass.field_of_study_abbreviation + ' ' + currentclass.course_number,
        instructor: currentclass.instructor_name.trim(),
        instructor: currentclass.instructor_name.trim(),
        field_of_study_abbreviation: currentclass.field_of_study_abbreviation.trim(),
        course_number: currentclass.course_number.trim(),
        unique_number: currentclass.unique_number.trim(),
        college_unit_code: currentclass.college_unit_code.trim(),
        course_title: currentclass.course_title.trim(),
        field_of_study: currentclass.field_of_study.trim(),
        backenddata: currentclass,
        allDay: false
      };
      return event;
    };
    var addEvent = function(currentclass, meeting, day, weekday) {
      var evt = getBaseEventObj(currentclass);
      evt.start = moment(day.format("YYYYMMDD") + moment(meeting.start_time, "Hmm").format("Hmm"), "YYYYMMDD Hmm").format("YYYY-MM-DDTHH:mm:ss");
      evt.end = moment(day.format("YYYYMMDD") + moment(meeting.end_time, "Hmm").format("Hmm"), "YYYYMMDD Hmm").format("YYYY-MM-DDTHH:mm:ss");
      evt.room = meeting.room.trim();
      evt.building = meeting.building.trim();
      evt.days = meeting.days.trim();
      evt.start_time = meeting.start_time;
      evt.end_time = meeting.end_time;
      evt.selDay = day;
      evt.weekday = weekday;
      //$scope.events.push(evt);
      //console.log("meeting room=" + evt.room + " ***building=" + evt.building);
      evt.mapUrl = $scope.buildingLocations[evt.building];
      console.log("search mapUrl = " + evt.mapUrl + " for building " + evt.building);
      // console.log(" evt.start" + evt.start);
      // console.log(" evt.end" + evt.end)
      $scope.events.push(evt);
      var wd = getWeekDay(weekday);
      currentclass.start = evt.start;
      currentclass.end = evt.end;
      currentclass.building = evt.building;
      currentclass.room = evt.room;
      currentclass.mapUrl = evt.mapUrl;
      if (wd) {
        wd.data.push(evt);
      }
    }

    function updateEvents(currentclass) {
      var meetings = currentclass.class_meetings;
      angular.forEach(meetings, function(meeting, mindex) {
        var days = meeting.days;
