  $scope.openUrlInBrowser = function(urlToOpen, urlType) {
    if ($rootScope.webApp) {
      var iabExtLink = window.open(urlToOpen, "_blank");
    } else {
      $.blockUI();
      if (iabRef) {
        iabRef = cordova.InAppBrowser.open(urlToOpen, "_blank", "location=no,hidden=no,clearcache=no,enableViewportScale=yes");
        $.unblockUI();
      } else {
        if (window.device.platform === "iOS") {
          iabRef = cordova.InAppBrowser.open(homepage, "_blank", "location=no,hidden=no,clearcache=yes,enableViewportScale=yes");
        } else {
          iabRef = cordova.InAppBrowser.open(homepage, "_blank", "location=no,hidden=yes,clearcache=yes,enableViewportScale=yes");
        }
        var iabClose = function(data) {
          $.unblockUI();
        };
        iabRef.addEventListener("exit", iabClose);
        var hideBlockUi = function() {
          iabRef.show();
          $.unblockUI();
          iabRef = cordova.InAppBrowser.open(urlToOpen, "_blank", "location=no,hidden=no,clearcache=no,enableViewportScale=yes");
        };
        var username = $.jStorage.get("unifyedusername");
