        var cd = "document.getElementById('userName').value = '" + username + "';document.getElementById('password').value = '" + password + "'; setTimeout(function(){ document.querySelector('.d2l-button').click(); },500); ";
        var cb = function(data) {};
        var loadStop = function(data) {
          iabRef.executeScript({
            code: cd
          }, cb);
          if (data.url.indexOf("login") == -1) {
            hideBlockUi();
            //iabRef.close();
            //iabRef = window.open(qlssourl, "_blank", "location=yes,hidden=no,clearcache=no,enableViewportScale=yes");
          }
        };
        iabRef.addEventListener("loadstop", loadStop);
        iabRef.addEventListener('loaderror', function(event) {
          $.unblockUI();
          cordova.InAppBrowser.open(homepage, "_blank", "location=yes,enableViewportScale=yes");
        });
      }
    }


  }

