function pageprocessorSchedule2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.ClickedMon=false;
    $scope.ClickedTue=false;
    $scope.ClickedWed=false;
    $scope.ClickedFri=false;
    $scope.ClickedThu=false;

    $scope.currentterm = null;
    var url = $rootScope.middlewareServerUrl + "/cmsClassScheduleWeeks";
    $rootScope.postAPI(url, "", function(data) {
        $scope.schTerms = data.WeekList;
        if (data.WeekList.length > 0) {
            $scope.selterm = data.WeekList[0].ID;
            $scope.termselect($scope.selterm);
        }
        $.unblockUI();
    });

    $scope.termselect = function(termcode) {
        $scope.courses = null;
        var url = $rootScope.middlewareServerUrl + "/cmsClassScheduleWeekClasses";
        var parameterData = "week=" + $scope.selterm;
        $rootScope.postAPI(url, parameterData, function(data) {
            try{$scope.courses = data.WeekClasses[0];
                $scope.AllClasses = data.AllClasses[0].Classes;
            $scope.selectDay('Mon');
            $.unblockUI();
        }catch(e){}
            //$scope.currentterm = data.currentterm[0];
        });
    }


    $scope.selectDay=function(id){
        try{
        $scope.ClickedMon=false;
    $scope.ClickedTue=false;
    $scope.ClickedWed=false;
    $scope.ClickedFri=false;
    $scope.ClickedThu=false;
        switch (id){
            case 'Mon':{
                $scope.ClickedMon=true;
                $scope.classes=$scope.courses.Mon;
                break;
            }
            case 'Tue':{
                $scope.ClickedTue=true;
                $scope.classes=$scope.courses.Tue;
                break;
            }
            case 'Wed':{
                $scope.ClickedWed=true;
                $scope.classes=$scope.courses.Wed;
                break;
            }
            case 'Thu':{
                $scope.ClickedThu=true;
                $scope.classes=$scope.courses.Thu;
                break;
            }
            case 'Fri':{
                $scope.ClickedFri=true;
                $scope.classes=$scope.courses.Fri;
                break;
            }
            case 'Sat':{
                $scope.classes=$scope.courses.Sat;
                break;
            }
            case 'Sun':{
                $scope.classes=$scope.courses.Sun;
                break;
            }
        }
        }catch(e){alert(e)}
    }


    $scope.classInfo=function(id){
    try{var classID = id.match( /"(.*?)"/ )[1];
    var weekCls=$scope.AllClasses;
    var splitClasses=weekCls.split("!");
    for(var i=0;i<splitClasses.length;i++){
        var classTime=splitClasses[i].split("|");
        if(classTime[0].split("_")[0] == classID){
            var html_view="Time: "+classTime[0].split("_")[1]+" - "+classTime[1];
            html_view +="<br/>"+classTime[2];
            html_view +="<br/>"+classTime[3];
            html_view +="<br/>"+classTime[4].split("_")[0];
            break;
        }
        
    }
    return $sce.trustAsHtml(html_view);
}catch(e){}
    }
}

