        //console.log(crse);
        $rootScope.courseid = crse.Id;
        $rootScope.coursename = crse.Name;
        $rootScope.category = category;

        var gurl = $rootScope.WebStudyURL + "/services/data/RVCC/WebStudy/WebStudyCourseSyllabus";
        $rootScope.postWSAPI(gurl,"courseid="+crse.Id,function(data) {
            $rootScope.syllabus = data.syllabus[0];
            $rootScope.syllabusinfo = data.syllabusinfo;            
            if (data.msgsjson && data.msgsjson.data.length > 0) {
                //needs confirmation. show dialog boxes
                $scope.showDialog = true;
                $scope.messages = data.msgsjson.data;
                $scope.msgIndex = 0;
                console.log($scope.messages[$scope.msgIndex][1]);
            } else {
                $location.path("/app/WebStudyCourses/WebStudyCoursesPage2");
            }

        });
    }

    $scope.iterateMsg = function() {
        if (!$scope.readMsg) {
            var gotoHome = function() {
                return;
            };
            if (window.device) {
                navigator.notification.alert("Please tick the checkbox 'I confirm that I have read' before proceeding.", gotoHome, 'My Courses', 'OK');
            } else {
                apprise("Please tick the checkbox 'I confirm that I have read' before proceeding.", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {
                    gotoHome();
                });
            }   
            return;         
        }
        var gurl = $rootScope.WebStudyURL + "/services/data/RVCC/WebStudy/WebStudyCourseConfirmAnnouncement";
        var newsid = $scope.messages[$scope.msgIndex][0];
        $rootScope.postWSAPI(gurl,"newsid="+newsid,function(data) {
             if ($scope.msgIndex < $scope.messages.length -1) {
               $scope.msgIndex++;
            } else {
                $location.path("/app/WebStudyCourses/WebStudyCoursesPage2");
            }           
        });         

    }

    $scope.trusthtml = function (data) {
        return $sce.trustAsHtml(data);
    } 

    $rootScope.back = function() {
        $rootScope.lastPage = "";
        $rootScope.courselist = "";
        if ($routeParams.from) {
           if (window.device) {
