function pageprocessorWebStudyCoursesPage3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    var appCont = angular.element(document.querySelector('.app-content'));
    appCont.addClass('content-area');
    $rootScope.sylfrompage = "page3"; 
    
    $scope.trustHtml = function(html) {
        return $sce.trustAsHtml(html);
    }

    $scope.forum = $rootScope.forum;
    $scope.forum.toggleclass = "fa fa-plus-circle fa-2x";
    $scope.forum.toggle = true;
    $scope.forumPosts = [];
    $scope.startnewthread = false;
    $scope.toggleSchedule = function(forum) {
        forum.toggle = !forum.toggle;
        if (forum.toggle) {
            forum.toggleclass = "fa fa-plus-circle fa-2x";
        } else {
            forum.toggleclass = "fa fa-minus-circle fa-2x";
        }
    }

    $scope.sanitizeForumPosts = function() {
        angular.forEach($scope.forumPosts, function(value, key) {
            value.toggleclass = "fa fa-plus-circle fa-2x";
            value.toggle = true;
        });
    }
    $scope.loadBoard = function() {
        $scope.startnewthread = false;
        var url = $rootScope.WebStudyURL + "/services/data/RVCC/WebStudy/WebStudyForumboard";
        var postdata = "forumId=" + $scope.forum.forumId;
        $rootScope.postWSAPI(url, postdata, function(data) {
            $scope.forumPosts = data.posts;
            $scope.startnewthread = data.startnewthread;
            $scope.sanitizeForumPosts();
            console.log($scope.forumPosts);
            $scope.normalizeImageUrl();
            $.unblockUI();
        });
    }
    $scope.loadBoard();
    $scope.normalizeImageUrl = function() {
        setTimeout(function() {
            var images = $("#appContent img");
            $.each(images, function(key, val) {
                if (val.getAttribute("src").indexOf("/webcourses/") === 0) {
                    val.src = "https://cms5.webstudy.com" + val.getAttribute("src");
                }
                console.log(val.src);
            });
        }, 2000);
    }
    $scope.showReply = function(post) {
        $scope.modaltype = "reply";
        $scope.subject = post.subject;
        $scope.message = "";
        $scope.curpost = post;
        $rootScope.toggle("replyThreadModal");
    }
    $scope.showStartNewThread = function() {
        $scope.modaltype = "createthread";
        $scope.subject = "";
        $scope.message = "";
        $rootScope.toggle("replyThreadModal");
    }
    $scope.replyThread = function() {
        if ($scope.modaltype === "reply") {
            if ($scope.message != "") {
                var url = $rootScope.WebStudyURL + "/services/data/RVCC/WebStudy/webStudyForumThreadReply2";
                var postdata = "forumId=" + $scope.forum.forumId + "&subject=" + encodeURIComponent($scope.subject) +
                    "&content=" + encodeURIComponent($scope.message) + "&replyid=" + $scope.curpost.id + "&msglevel=" + $scope.curpost.msglevel;
                $rootScope.postWSAPI(url, postdata, function(data) {
                    console.log(data);
                    $scope.loadBoard();
                    $rootScope.toggle("replyThreadModal");
                    $.unblockUI();
                });
            }
        }else if ($scope.modaltype === "createthread") {
            if ($scope.message != "") {
                var url = $rootScope.WebStudyURL + "/services/data/RVCC/WebStudy/webStudyForumCreateNewThread";
                var postdata = "forumId=" + $scope.forum.forumId + "&subject=" + encodeURIComponent($scope.subject) +
                    "&content=" + encodeURIComponent($scope.message);
                $rootScope.postWSAPI(url, postdata, function(data) {
                    console.log(data);
                    $scope.loadBoard();
                    $rootScope.toggle("replyThreadModal");
                    $.unblockUI();
                });
            }
        }
    }
}

