                $scope.newProfileBackground = e.target.result;
                //$('#profileImg').attr("src", $scope.newProfileImage);

                $scope.currentUser.profileBackground = e.target.result;
                $scope.$apply();
              };

              if (this.files[0].size > 5000000) {
                if (window.device) {
                  navigator.notification.alert("Image size is too large.  Please upload below 5MB");
                } else {
                  alert("Image size is too large.  Please upload above 500KB");
                }
                return;
              }
              if (this.files[0].size < 100000) {

                if (window.device) {
                  navigator.notification.alert("Image size is too small. Please upload above 100KB");
                } else {
                  alert("Image size is too small. Please upload above 100KB");
                }
                return;
              }
              FR.readAsDataURL(this.files[0]);
              $("#uploadProfileCvr")[0].value = '';
            } else {
              if (window.device) {
                navigator.notification.alert("Only png, jpg file types are allowed.", null, '', 'Ok');
              } else {
                alert("Only png, jpg file types are allowed.");
              }
            }
          }
        });
        document.getElementById("uploadProfileCvr").click();
      } catch (e) {
        alert(e);
      }
    }
  };

  function updateUserProfile(callBack) {
    url = '/unifydidentity/user';
    $rootScope.callAPI(url, 'POST', $scope.currentUser, function(res) {
      $.unblockUI();
      if (!res.data) {
        if (window.device) {
          navigator.notification.alert(
            'Unable to register user.', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('Unable to register user.');
        }
        return callBack("Error");
      }
      var data = res.data;
      $rootScope.user['id'] = data['id'];
      $rootScope.user['profileBackground'] = data['profileBackground'];
      $rootScope.user['firstName'] = data['firstName'];
      $rootScope.user['lastName'] = data['lastName'];
      $rootScope.user['profileImage'] = data['profileImage'];
      $rootScope.user['role'] = data['role'];
      $rootScope.user['tenant'] = data['tenant'];
      $rootScope.user['email'] = data['email'];
      $rootScope.user['phone'] = data['phone'];
      $rootScope.user['areaOfInterest'] = data['areaOfInterest'];
      $rootScope.user['channels'] = data['channels'];
      return callBack();
    });
  }



  $scope.preselected = function(index) {
    if (!$scope.areaOfInterest[index].enable)
      return;

    $scope.isDirty = true;
    $scope.areaOfInterest[index].selected = !$scope.areaOfInterest[index].selected;
    if ($scope.areaOfInterest[index].selected)
      $scope.selectedCount++;
    else
      $scope.selectedCount--;

  };

  $scope.cancelEdit = function() {
    //$scope.currentUser.profileImage = "";
    $scope.currentUser['profileBackground'] = $scope.currentUserTemp['profileBackground'];
    $scope.currentUser['profileImage'] = $scope.currentUserTemp['profileImage'];
    $scope.currentUser['areaOfInterest'] = $scope.currentUserTemp['areaOfInterest'];
    $scope.currentUser['profileImageCover'] = $scope.currentUserTemp['profileImageCover'];

    //console.log(JSON.stringify($scope.currentUserTemp));
    //console.log(JSON.stringify($scope.currentUser));
    $scope.editMode = false;
    $scope.isDirty = false;
    $scope.newProfileImage = "";
    for (var i = 0; i < $scope.areaOfInterest.length; i++) {
      $scope.areaOfInterest[i].selected = false;
      angular.forEach($scope.currentUser.areaOfInterest, function(value, key) {
        if (value == $scope.areaOfInterest[i].id) {
          $scope.areaOfInterest[i].selected = true;
        }
      });
    }
    console.log("55555---")

    $scope.$apply();
  }




  var windowWidth = $(window).width();
  setTimeout(function() {
    $('.u8PopupBg ').on('show.bs.modal', function(e) {
      setTimeout(function() {
        $(".modal-backdrop.in").hide();
      }, 500);
    });
  }, 500);
  //fix modal popup issue
  setTimeout(function() {
    if (windowWidth < 768) {
      $('.u8PopupBg ').on('show.bs.modal', function(e) {
        $('.owl-stage-outer').addClass('remove-carousel');
        $('.owl-stage').addClass('remove-carousel');
      });

      $('.u8PopupBg ').on('hidden.bs.modal', function(e) {
        $('.owl-stage-outer').removeClass('remove-carousel');
        $('.owl-stage').removeClass('remove-carousel');
      });
    }
  }, 1000);

}

function pageprocessorYouTube20(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location, services, studioParam) {
  $scope.studioParam = studioParam;
  $("#fixedFooterContent").html('');
  $rootScope.videos = "";
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  if ($rootScope.loggedIn) {
    $scope.userRoles = $rootScope.user.role.map(function(r) {
      return r.toLowerCase();
    });
  } else {
    $scope.userRoles = [];
  }

  console.log(JSON.stringify($scope.userRoles));
  $rootScope.videosChannelsList = pagedef.channelsList;

  $scope.loadChannel = function(channel) {
    var channelId = channel.channelId;
    $scope.selectedChannel = channel.label;
    var videoUrl = "https://www.googleapis.com/youtube/v3/search?videoEmbeddable=true&order=date&part=snippet&channelId=" + channelId + "&type=video&maxResults=50&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw";

    $.blockUI();
    $http({
      'url': videoUrl,
      'method': 'GET'
    }).then(function successCallBack(data) {
      $.unblockUI();
      var data = data.data;
      if (data.items.length == 0) {
        if (window.device) {
          navigator.notification.alert(
            'No video found for given channel id.',
            null,
            '',
            'Ok'
          );
        } else
          alert('No video found for given channel id.');

        return;
      }

      if ($rootScope.webApp != undefined) {
        for (var i = 0; i < data.items.length; i++) {
          data.items[i].playing = false;
        }
        $scope.videos = data.items;
        $scope.videos[0].playing = true;
        $scope.loadVideo(0);
      } else {
        $scope.videos = data.items;
        $rootScope.videos = data.items;
      }



    }, function errorCallBack(data) {
      $.unblockUI();
      if (window.device) {
        navigator.notification.alert(
          'Incorrect channel id.',
          null,
          '',
          'Ok'
        );
      } else
        alert('Incorrect channel id');
      window.history.back();
    });
  }


  $scope.checkUserHasAccess = function(role) {
    role = role.toLowerCase().split(',');
    var found = false;
    $scope.userRoles.forEach(function(val) {
      if (role.indexOf(val) != -1) {
        found = true;
      }
    });
    return found;
  }

  for (var i = 0; i < $rootScope.videosChannelsList.length; i++) {
    var found = false;
    if ($scope.checkUserHasAccess($rootScope.videosChannelsList[i].role) || $rootScope.videosChannelsList[i].role.toLowerCase().trim() == 'public') {
      var found = true;
      $scope.loadChannel($rootScope.videosChannelsList[i]);
      break;
    }
  }

  $scope.navigate = function(index) {
    if ($rootScope.webApp != undefined) {
      $scope.loadVideo(index);
    } else {
      $rootScope.index = index;
      $location.path("/app/YouTube20/YouTube20VideosDetails");
    }
    var scroll_pos = ($('#myHeader').position().top);
    $("#appContent , #pageContent").animate({
      scrollTop: (scroll_pos)
    }, "slow");
  }


  $scope.loadVideo = function(index) {
    for (var i = 0; i < $scope.videos.length; i++) {
      $scope.videos[i].playing = false;
    }
    var video = $scope.videos[index];

    video.playing = true;
    var videoID = video.id.videoId;
    $scope.publishDate = videoID.publishedAt;
    var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw&part=snippet,contentDetails,statistics,status";
    $http({
      'method': 'GET',
      'url': url
    }).then(function successCallBack(data) {
      var data = data.data;
      $scope.title = data.items[0].snippet.localized.title;
      $scope.publishDate = data.items[0].snippet.publishedAt;
      $scope.trustSrc = function() {
        return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0&autoplay=1");
      };
      $scope.description = data.items[0].snippet.localized.description;
    });



  }
  setTimeout(function() {
    $('.dropdown').keyup(function(e) {
      if (e.keyCode == 40) {
        $(this).addClass('open')
      } else if (e.keyCode == 38) {
        $(this).removeClass('open')
      }
    });
  }, 1000);

}

function pageprocessorYouTube20VideosDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  try {

    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    try {
      var onPause = function() {
        $("iframe").attr("src", $("iframe").attr("src"));
      };
      document.addEventListener("pause", onPause, false);
    } catch (e) {}
    $scope.showDetails = false;
    $scope.hideDetails = false;
    $scope.showDescription = function() {
      $scope.showDetails = true;
    }

    $scope.loadVideo = function(index) {
      for (var i = 0; i < $rootScope.videos.length; i++) {
        $rootScope.videos[i].playing = false;
      }

      $rootScope.videos[index].playing = true;
      $scope.videoInfo = $rootScope.videos[index];
      var videoID = $scope.videoInfo.id.videoId;

      $scope.trustSrc = $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0");

      var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw&part=snippet,contentDetails,statistics,status";
      $http({
        'url':url,
        'method':'GET'}).then(function successCallBack(data) {
        var data = data.data;
        $scope.title = data.items[0].snippet.localized.title;
        $scope.publishDate = data.items[0].snippet.publishedAt;
        $scope.description = data.items[0].snippet.localized.description;
        $('.scrollable-content').animate({
                    scrollTop: 0
                }, 'slow');
      },function errorCallBack(data) {
        $.unblockUI();
      });
    }

    $scope.loadVideo($rootScope.index);
    $scope.openVideoInWeb = function(id){
      cordova.InAppBrowser.open('https://www.youtube.com/watch?v='+id, "_system");
    }
  } catch (e) {
    console.log(e);
  }
}

