function pageprocessorEventsiCal8(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $("#fixedFooterContent").html('');
  /*var template = pagedef.pageTemplate;
   var data1 = $compile($(template))($scope);
   $("#appContent").append(data1);*/

  var showHash = ($rootScope.webApp) ? "/" : "#";
  var dataTemplateCode = '<li ng-if="index >= 0 "> <a id="event{{index}}" data-index="{{index}}" class="list-group-item " href="' + showHash + 'app/EventsiCal8/EventsiCal8Details?index={{index}}" ><h4>{{title}}</h4>   <p>{{displaytime}}</p></a></li>'


  jQuery.fn.extend({
    size: function() {
      return this.length;
    }
  });

  function formatAMPM(date) {
    var hours = date.getHours();
    var minutes = date.getMinutes();
    var ampm = hours >= 12 ? 'pm' : 'am';
    hours = hours % 12;
    hours = hours ? hours : 12; // the hour '0' should be '12'
    minutes = minutes < 10 ? '0' + minutes : minutes;
    var strTime = hours + ':' + minutes + ' ' + ampm;
    return strTime;
  }
  var weekDays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  var monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
  var dayStr;
  var monthStr;
  var dateStr;
  var timeStr;
  var currDate;

  var recurringEventsData = [];
  var index = 0;

  function findAllRecurringEvents(recEvent) {
    var data1 = [];
    var startDate = getDTStart(recEvent);
    if (startDate.length == 8) {
      startDate = startDate + "T050000";
    }
    var freq = recEvent.RRULE;
    var ruleString = freq + ";DTSTART=" + startDate + "Z";
    var rule = RRule.fromString(ruleString);
    // var rule1 = rule.all();
    var rule1 = rule.between(new Date(2012, 1, 1), new Date(2027, 1, 1));
    for (var j = 0; j < rule1.length; j++) {
      var eventdate = rule1[j];
      index = index + 1;
      var today = new Date();
      var eveDate = eventdate.toString();

      currDate1 = new Date(eveDate);
      dayStr1 = weekDays[currDate1.getDay()];
      monthStr1 = monthNames[currDate1.getMonth()];
      dateStr1 = currDate1.getDate();
      timeStr1 = formatAMPM(currDate1);

      if (new Date(eveDate) > today) {
        data1.push({
          "link": recEvent.link,
          "location": recEvent.LOCATION,
          "displaydate": eventdate.toString(),
          "displaytime": monthStr1 + " " + dateStr1 + "," + currDate1.getFullYear(),
          "date": eventdate.getTime().toString(),
          "title": recEvent.SUMMARY,
          "description": recEvent.DESCRIPTION,
          "index": index
        });
      }
    }
    recurringEventsData = data1.concat(recurringEventsData);
  }

  $.blockUI();
  $rootScope.isblocking = true;
  var url = "https://kryptosda.kryptosmobile.com/kryptosds/ical2json/convert";
  var proxyMethod = "POST";
  //var icsUrl = "https://calendar.google.com/calendar/ical/dtcccal%40gmail.com/public/basic.ics";
  var icsUrl = pagedef.url;

  var proxyData = {
    "icalurl": icsUrl
  };

  function sort_by(field, reverse, primer) {
    var key = primer ?
      function(x) {
        return primer(x[field]);
      } : function(x) {
        return x[field];
      };

    reverse = reverse ? -1 : 1;

    return function(a, b) {
      return a = key(a), b = key(b), reverse * ((a > b) - (b > a));
    };
  }

  $http({
    method: proxyMethod,
    url: url,
    data: proxyData
  }).
  then(function successCallBack(data1, status, headers, config) {
      $rootScope.isblocking = false;
      $.unblockUI();
      console.log(data1);
      var data1 = data1.data;
      $scope.ical2json = data1;
      var data = [];

      var ecollection = $scope.ical2json.output.VEVENT;
      if (!ecollection) {
        ecollection = $scope.ical2json.output.VCALENDAR[0].VEVENT;
      }
      $.each(ecollection, function(key, val) {
        //alert(JSON.stringify(val));
        var dtstart = getDTStart(val);
        if (val.RRULE) {
          findAllRecurringEvents(val);
        }
        //console.log(dtstart);
        var pdate = iCalDateParser(dtstart);
        //console.log(dtstart + " : " + pdate);
        dayStr = "";
        monthStr = "";
        dateStr = "";
        timeStr = "";
        if (pdate) {
          currDate = new Date(pdate);
          dayStr = weekDays[currDate.getDay()];
          monthStr = monthNames[currDate.getMonth()];
          dateStr = currDate.getDate();
          timeStr = formatAMPM(currDate);
        }
        if (pdate != null) {
          data.push({
            "link": val.URL,
            "location": val.LOCATION,
            "displaydate": dayStr + " " + monthStr + " " + dateStr,
            "formattedDate": monthStr + " " + dateStr + "," + currDate.getFullYear(),
            "displaytime": monthStr + " " + dateStr + "," + currDate.getFullYear(),
            "date": pdate.getTime().toString(),
            "title": val.SUMMARY,
            "description": val.DESCRIPTION
          });
          index = index + 1;
        }

      });

      if (recurringEventsData) {
        data = data.concat(recurringEventsData);
      }

      $("#appContent").append($(pagedef.pageTemplate));
      $.unblockUI();
      data = data.sort(sort_by('eventdate', false, null));

      for (var i = 0; i < data.length; i++) {
        data[i]['index'] = i;
      }
      console.log(data);
      $rootScope.EventsiCal8Eventsdata = data;
      $("#EventsiCal8EventsListData").eventCalendar({
        jsonData: data,
        showDescription: true,
        dataTemplate: dataTemplateCode,
        eventsLimit: 20,
        startWeekOnMonday: false,
        moveSpeed: 0,
        moveOpacity: 0
      });

      console.log("angular Check");

      /*This timeout is use for fix 2layout breakdown   */
