    $scope.description = $rootScope.EventsiCal8Eventsdata[$routeParams.index - 1].description;
    $scope.link = $rootScope.EventsiCal8Eventsdata[$routeParams.index - 1].link;*/
  $scope.selEvent = $rootScope.EventsiCal8Eventsdata[$routeParams.index];

  console.log('selevent = '+$routeParams.index);

  try {
    $scope.getCurrentDateEvents = function(EventsiCal8Eventsdata, selEvent) {
      var events = [];

      for (var i = 0; i < EventsiCal8Eventsdata.length; i++) {
        var eventDate = new Date(EventsiCal8Eventsdata[i]['displaydate']);
        var seldate = new Date(selEvent['displaydate']);
        eventDt = eventDate.getDate()+"-"+eventDate.getMonth()+"-"+eventDate.getFullYear();
        seldt = seldate.getDate()+"-"+seldate.getMonth()+"-"+seldate.getFullYear();
        if (eventDt === seldt) {
          events.push(EventsiCal8Eventsdata[i]);
        }
      }
      return events.reverse();

    };

    $scope.getIndex = function(currentDateEvents, selEvent) {
      var index = -1;
      for (var i = 0; i < currentDateEvents.length; i++) {
        if (currentDateEvents[i]['index'] === selEvent['index']) {
          index = i;
          break;
        }
      }
      return index;
    };


    $scope.currentDateEvents = $scope.getCurrentDateEvents($rootScope.EventsiCal8Eventsdata, $scope.selEvent);
    
    console.log(JSON.stringify($scope.currentDateEvents));

    $scope.index = $scope.getIndex($scope.currentDateEvents, $scope.selEvent);

    console.log($scope.currentDateEvents);
    console.log($scope.index);
  } catch (e) {
    console.log(e);
  }

  $scope.openurl = function() {
    window.open($scope.selEvent.link, "_blank");
  };

  var data = pagedef.pageTemplate;
  data = $(data).append($(pagedef.datatemplate));
  console.log(JSON.stringify(data));
  var data1 = $compile($(data))($scope);
  $("#appContent").append(data1);

  $scope.trustHtml = function(data) {
    if (!data) {
      return data;
    }

    data = data.replace(/(?:\\r\\n|\\r|\\n)/g,  '<br/>');
    data = data.replace(/(?:\\)/g,  '');
    return $sce.trustAsHtml(data);
  }

  $scope.openFeed = function() {
    $location.path("/app/EventsiCal8/EventsiCal8");
    return;
  };

  $scope.next = function() {

    if ($scope.index === $scope.currentDateEvents.length - 1)
      return;

    $scope.index++;
    $routeParams.index++;
    $scope.selEvent = $scope.currentDateEvents[$scope.index];

    $window.scrollTo(0, 0);
  };

  $scope.prev = function() {
    if ($scope.index === 0)
      return;

    $scope.index--;
    $routeParams.index--;
    $scope.selEvent = $scope.currentDateEvents[$scope.index];
    $window.scrollTo(0, 0);
  };
}

function pageprocessorNews343(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $("#fixedFooterContent").html('');
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $.blockUI();
  var months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

  var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
  var param = {
    "url": pagedef.url
  };

  $http.post(rssreaderUrl, param, '').then(function successCallBack(res, status, headers, config) {
    try {
      var data = res.data;
      for (var i = 0; i < data.feed.entries.length; i++) {
        var date = data.feed.entries[i]['pubDate'] || data.feed.entries[i]['published'];
        
		if (angular.isDefined(date)) {
          data.feed.entries[i]['date'] = new Date(date.replace(/-/g, '/'));
        }
      }
    } catch (e) {}

    $scope.feeds = data.feed.entries;
    $rootScope.News343feeds = data.feed.entries;
    $.unblockUI();
  }, function errorCallBack(error, status, header, config) {
    $.unblockUI();
  });

  $scope.openFeed = function(index) {
    $rootScope.News343feedDetails = $scope.feeds[index];
    $rootScope.News343index = index;
    $scope.content = $rootScope.News343feedDetails.content;
    $location.path("/app/News343/News343Details");
  };

  $scope.trustHTML = function(html) {
    if (html.indexOf('meta') != -1) {
      return '';
    } else {
      try {
        var i = html.indexOf('<script');
        var j = html.indexOf('</script');
        console.log(i);
        console.log(j);
        if (i != -1 && j != -1) {
          html = html.substr(0, i) + " " + html.substr(j + 9);
        }
      } catch (e) {
        console.log(e)
      }
      console.log(html);
      var data = "<div>" + html + "</div>";
      return $sce.trustAsHtml(data);
    }
  }

  $scope.getFeedDate = function(f) {
    return f.date.getDate();
  };

  $scope.getFeedMonth = function(f) {
    return months[f.date.getMonth()];
  };
  $scope.date = new Date();

  $scope.getFeedTime = function(f) {
    var date = f.date.toString();
    date = date.split(" ")[4];
    return $scope.tConvert(date);
  };

  $scope.tConvert = function(time) {
    time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

    if (time.length > 1) {
      time = time.slice(1);
      time[5] = +time[0] < 12 ? ' AM' : ' PM';
      time[0] = +time[0] % 12 || 12;
    }
    return time.join('');
  }

  $scope.backgroundImage = function(feed) {
    if (feed.content.indexOf("img") != -1) {
      var url = feed.content.substring(feed.content.indexOf("src") + 5, feed.content.indexOf('"', feed.content.indexOf("src") + 5));
      return {
        'background': 'url(' + url + ') no-repeat',
        'background-size': 'cover',
        'height': '250px',
      }
    }
  }

}

