function pageprocessorcannoneerAthletics(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
   // $.blockUI();
    try {
        $rootScope.back = function() {
            $rootScope.leftToRight = true;
            window.history.back();
        }
        console.log(pagedef.feeds);
        $scope.feedlist = pagedef.feeds;
        $rootScope.feedlist = pagedef.feeds;
      //  $rootScope.isblocking = true;
        $scope.selTab = 1;
        $rootScope.showlogin = false;
      //  $.unblockUI();

        //$rootScope.schfeeds = null;
       // $rootScope.scofeeds = null;
      //  $rootScope.newsfeeds = null;

        $rootScope.selTabAthletics = $rootScope.selTabAthletics != undefined ? $rootScope.selTabAthletics : 'tab1';
        console.log($rootScope.selTabAthletics);


        $rootScope.indexAthletics = $rootScope.indexAthletics != undefined ? $rootScope.indexAthletics : 0;
        var f = $rootScope.feedlist[$rootScope.indexAthletics];
        console.log("f=");
        console.log(f);
        //$rootScope.appDisplayName = f.name;
        $rootScope.selSports = f.name;


        $scope.fetchSportData = function(index) {
            f = $rootScope.feedlist[index];
            $rootScope.indexAthletics = index;
            var newsUrl = f.newsurl;
            //$rootScope.appDisplayName = f.name;
            console.log($rootScope.appDisplayName);
            $rootScope.selSports = f.name;
            console.log($rootScope.selSports);
            $scope.loadnews();
            $scope.loadsch();
            $scope.loadsco();
            $scope.$apply();
        };

        $scope.getimage = function(content) {

            return $sce.trustAsHtml(content.split('"')[1]);
        }
        $scope.trustUrl = function(url) {

            return $sce.trustAsUrl(url);
        }
        $scope.trusthtml = function(data) {
            return $sce.trustAsHtml(data);
        };

        $scope.loadnews = function() {

            var newsUrl = f.newsurl;
            //$.blockUI();
            $rootScope.isblocking=true;
            var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
            var param = {
                "url": newsUrl
            };
            $http.post(rssreaderUrl, param, '').success(function(data, status, headers, config) {
                $scope.newsfeeds = data.feed.entries;
                console.log($scope.newsfeeds.length);
                $rootScope.newsfeeds = data.feed.entries;
                $.unblockUI();
                $rootScope.isblocking=false;
            }).error(function(error, status, header, config) {

                $.unblockUI();
            });

        };

        $scope.loadsch = function() {

             //$.blockUI();
            var currYear = (new Date().getFullYear() + '').substring(2);
            var prevYear = parseInt(new Date().getFullYear()) - 1;
            console.log("currYear=" + currYear);
            console.log("prevYear:" + prevYear);

            var url = f.schurl.replace("{{curr-next}}", prevYear + "-" + currYear);
            console.log("url: " + url);
            if (window.device) {} else {
                url = "/websimulator/xml?url=" + url;
            }


            var xhr = $.ajax({
                url: url,
                dataType: "xml",
                async: true,
                data: {},
                success: function(resp) {
                    var results = $.xml2json(resp);
                    console.log(results);
                    $scope.schfeeds = results;
                    $rootScope.schfeeds = results;


                    $.unblockUI();
                },
                error: function(e) {

                    $.unblockUI();

                }
            });


        };


        $scope.loadsco = function() {

            $.blockUI();

            var currYear = (new Date().getFullYear() + '').substring(2);
            var prevYear = parseInt(new Date().getFullYear()) - 1;
            var url = f.schurl.replace("{{curr-next}}", prevYear + "-" + currYear);
            if (window.device) {} else {
                url = "/websimulator/xml?url=" + url;
            }


            var xhr = $.ajax({
                url: url,
                dataType: "xml",
                async: true,
                data: {},
                success: function(resp) {
                    var results = $.xml2json(resp);
                    var feedResults = results.channel.item;
                    var now = new Date();
                    var filteredResult = [];
                    $.each(feedResults, function(key, val) {


                        var feedDate = new Date(val.date);
                        if (feedDate < now) {

                            filteredResult.push(val);
                        }


                    });
                    $scope.scofeeds = filteredResult;
                    $rootScope.scofeeds = filteredResult;
                    try {
                        $scope.$apply();
                    } catch (e) {
                        console.log(e);
                    }


                    $.unblockUI();
                },
                error: function(e) {

                    $.unblockUI();

                }
            });


        };
        var data = "<div></div>";
        data = $(data)
            .append($(pagedef.datatemplate));
        var data1 = $compile($(data))($scope);
        $("#appContent")
            .append(data1);
        console.log($rootScope.newsfeeds == undefined);
        //$.blockUI();
        if ($rootScope.newsfeeds == undefined) {
            $scope.loadnews();
        } else {
            $scope.newsfeeds = $rootScope.newsfeeds;
            $scope.appDisplayName = $rootScope.appDisplayName;
            console.log($scope.appDisplayName);
            $scope.selSports = $rootScope.selSports;
        }
        console.log($rootScope.schfeeds == undefined);
        if ($rootScope.schfeeds == undefined) {
            $scope.loadsch();
        } else {
            $scope.schfeeds = $rootScope.schfeeds;
        }
        console.log($rootScope.scofeeds == undefined);
        if ($rootScope.scofeeds == undefined) {
            $scope.loadsco();
        } else {
            $scope.scofeeds = $rootScope.scofeeds;
        }


        $scope.convertToDate = function(stringDate) {

            var dateOut = new Date(stringDate);


            var hr = dateOut.getUTCHours();
            var mint = dateOut.getUTCMinutes();
            if (hr == 4 && mint == 0) {
                dateOut.setDate(dateOut.getDate());
                var dateString = dateOut.toString().substring(0, 16);

                var dateTimeString = dateString + "  TBA";
                return dateTimeString;

            } else {
                dateOut.setDate(dateOut.getDate());
                return dateOut;
            }
        };

        $scope.convertToDateNews = function(stringDate) {
            var dateOut = new Date(stringDate);
            dateOut.setDate(dateOut.getDate());
            return dateOut;
        };
        $scope.filterTitle = function(title) {
            var filText = title.replace("Jefferson Community College vs.", "").replace("vs. Jefferson Community College", "");
            return filText;
        };

        $scope.selectTab = function(tab) {
            $("#tab1").removeClass("toolbar-column-active");
            $("#tab2").removeClass("toolbar-column-active");
            $("#tab3").removeClass("toolbar-column-active");
            $("#" + tab).addClass("toolbar-column-active");
            if (tab == "tab1") {
                $scope.selTab = 1;
                $rootScope.selTabAthletics = 'tab1';
            } else if (tab == "tab2") {
                $scope.selTab = 2;
                $rootScope.selTabAthletics = 'tab2';
            } else if (tab == "tab3") {
                $scope.selTab = 3;
                $rootScope.selTabAthletics = 'tab3';
            }
        }

        $scope.selectTab($rootScope.selTabAthletics);
        
    } catch (e) {
        console.log(e)
    }
}

