function pageprocessorAppLauncher(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  if ($rootScope.loggedIn) {
    $scope.userRoles = $rootScope.user.role.map(function(r) {
      return r.toLowerCase();
    });
  } else {
    $scope.userRoles = [];
  }


  $scope.links = pagedef.appsList;

  $scope.checkUserHasAccess = function(role) {
    role = role.toLowerCase().split(',');
    var found = false;
    $scope.userRoles.forEach(function(val) {
      if (role.indexOf(val) != -1) {
        found = true;
      }
    });
    return found;
  }

  $scope.launchApp = function(app) {
    var appInfo = app;
    if (window.device.platform == "iOS") {
      var iosUri = appInfo.iOSUrlSchema;
      window.plugins.launcher.canLaunch({
        uri: iosUri
      }, function() {
        window.plugins.launcher.launch({
          uri: iosUri
        });
      }, function() {
        $scope.loadurl(appInfo.iOSUrl);
      });
    } else if (window.device.platform == "Android") {
      var androidpackage = appInfo.androidPackage
      window.plugins.launcher.canLaunch({
        packageName: androidpackage
      }, function() {
        window.plugins.launcher.launch({
          packageName: androidpackage
        });
      }, function() {
        $scope.loadurl(appInfo.androidUrl);
      });
    } else {
      apprise("This feature is not available on Emulator", {
        "verify": false,
        "textYes": "Ok"
      }, function(r) {});
    }
  }


  $scope.loadurl = function(url) {
    if (window.device) {
      $.blockUI();
    }
    var iabRef = window.open(url, device.platform == "Android" ? "_system" : "_blank", "location=no,hidden=yes");
    var hideBlockUi = function() {
      iabRef.show();
      $.unblockUI();
    };
    var iabClose = function(data) {
      //window.location.href = "index.html#home";
      $.unblockUI();
    };
    iabRef.addEventListener("exit", iabClose);
    var loadStop = function(data) {
      hideBlockUi();
    };
    iabRef.addEventListener("loadstop", loadStop);
  }
}

