function pageprocessorFinalGrade119(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  var config = {
    "middlewareServerUrl": pagedef.middlewareServerUrl,
    "product": pagedef.product
  }
	config.tokenType = "oauth";
  $rootScope.demoMode = false;
  $rootScope.middlewareServerUrl = pagedef.middlewareServerUrl;

  // console.log("$rootScope.middlewareServerUrl=" + $rootScope.middlewareServerUrl);

  /* Assign configuration params values to local variables */
  var middlewareUrl = pagedef.middlewareServerUrl;
  var licenseKey = pagedef.licenseKey;


  /* Declare page level variables  */
  var endpoint = "/bannerfinalgradetermdropdown";
  var bannerfinalgradeEndpoint = "/bannerfinalgrade/termCode=";
  //  $scope.selterm = "";
  $scope.selterm = "Select Term";
  $scope.selTab = 1;
  $scope.seltermcode = "";
  $scope.studentInfo = null;
  $scope.finalgrades = null;
  $scope.currentsummary = null;
  $scope.cumulativesummary = null;
  $scope.transfersummary = null;
  $scope.overallsummary = null;


  $scope.selTabAcdamic = $scope.selTabAcdamic != undefined ? $scope.selTabAcdamic : 'tab1';
  $scope.indexTerm = $scope.indexTerm != undefined ? $scope.indexTerm : 0;

  $scope.title = "Final Grade"

  /*function to show alert message*/
  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, $scope.title, null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: $scope.title,
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }
    }
  }

  $.blockUI();

  $rootScope.getAPI(config, endpoint, function(data) {
    $scope.gradesdropdown = data.dropdown;

    if (data) {
      if (data.dropdown.length >= 1) {
        $scope.termselect($scope.indexTerm);
        $scope.$apply();
      }
    } else {
      showAlert("No information is available for your account.")
    }

  });


  $scope.termselect = function(index) {
    $.blockUI();
    $('#clickName').blur();
    var t = $scope.gradesdropdown[index];
    $scope.selterm = t.TERM_DESC;


    if ($scope.selterm == "" || $scope.selterm == undefined) {
      $scope.studentInfo = null;
      $scope.finalgrades = null;
      $scope.currentsummary = null;
      $scope.cumulativesummary = null;
      $scope.transfersummary = null;
      $scope.overallsummary = null;
      return;
    }

    $rootScope.getAPI(config, bannerfinalgradeEndpoint + $scope.gradesdropdown[index].TERM_CODE, function(data) {
      if (data) {
        var tempColl = [];
        angular.forEach(data.finalgrade, function(value, key) {
          if (value.FINAL_GRADE) {
            tempColl.push(value);
          }
        });
        data.finalgrade = tempColl;
        $scope.studentInfo = data.studentInfo;
        if (middlewareUrl == "https://kryptosda.kryptosmobile.com/kryptosds") {
          $scope.studentInfo = [{
            "STUDY_PATH": "Bachelor of Arts",
            "LEVEL_DESC": "Undergraduate",
            "PROGRAM_NAME": "BA-HIST",
            "ADMIT_TERM": "Spring 2016",
            "CATLG_TREM": "Spring 2016",
            "COLLEGE": "Arts Science",
            "CAMPUS": "MAIN"
          }];
        }
        $scope.finalgrades = data.finalgrade;
        $scope.currentsummary = data["summary.current"];
        $scope.cumulativesummary = data["summary.cumulative"];
        $scope.transfersummary = data["summary.transfer"];
        $scope.overallsummary = data["summary.overall"];


      } else {
        showAlert("No information is available for your account.")
      }


    });

  }


  $scope.selectTab = function(tab) {
    $("#tab1").removeClass("toolbar-column-active");
    $("#tab2").removeClass("toolbar-column-active");
    $("#tab3").removeClass("toolbar-column-active");
    $("#" + tab).addClass("toolbar-column-active");
    if (tab == "tab1") {
      $scope.selTab = 1;
      $scope.selTabAcdamic = 'tab1';
    } else if (tab == "tab2") {
      $scope.selTab = 2;
      $scope.selTabAcdamic = 'tab2';
    } else if (tab == "tab3") {
      $scope.selTab = 3;
      $scope.selTabAcdamic = 'tab3';
    }
  }

}

