        if (data.p_schedule_out[0].currentschedule && typeof data.p_schedule_out[0].currentschedule == "string") {
          $scope.currentSchedule = JSON.parse(data.p_schedule_out[0].currentschedule);
        }

        $scope.todayCount = 0;
        $scope.tomorrowCount = 0;
        $scope.onlineTodayCount = 0;
        $scope.onlineTomCount = 0;
        angular.forEach(data.weekataglance, function(value, key) {
          let ClassStartDate = convertDate(value.START_DATE);
          let ClassEndDate = convertDate(value.END_DATE);
          console.log("start date-" + ClassStartDate + " - End Date - " + ClassEndDate);
          value.dropAllowed = $scope.isCourseDroppable(value.CRN);
          let currentDate = new Date();
          let dayIndex;
          let weekDay;
          // check if course is active for today and tom
          let sysDate = new Date();
          sysDate.setHours(0, 0, 0, 0);
          if (sysDate >= ClassStartDate && sysDate <= ClassEndDate) {
            dayIndex = (sysDate.getDay() == 0) ? 6 : sysDate.getDay() - 1;
            weekDay = $scope.weekdays[dayIndex];
            value.courseActiveToday = true;
            switch (weekDay) {
              case value.MON_DAY:
                value.today = true;
                break;
              case value.TUE_DAY:
                value.today = true;
                break;
              case value.WED_DAY:
                value.today = true;
                break;
              case value.THU_DAY:
                value.today = true;
                break;
              case value.FRI_DAY:
                value.today = true;
                break;
              case value.SAT_DAY:
                value.today = true;
                break;
              case value.SUN_DAY:
                value.today = true;
                break;
              default:
                value.today = false;
            }
            if (value.today) $scope.todayCount++;
          }
          sysDate.setDate(sysDate.getDate() + 1);
          if (sysDate >= ClassStartDate && sysDate <= ClassEndDate) {
            dayIndex = (sysDate.getDay() == 0) ? 6 : sysDate.getDay() - 1;
            weekDay = $scope.weekdays[dayIndex];
            value.courseActiveTom = true;
            switch (weekDay) {
              case value.MON_DAY:
                value.tomorrow = true;
                break;
              case value.TUE_DAY:
                value.tomorrow = true;
                break;
              case value.WED_DAY:
                value.tomorrow = true;
                break;
              case value.THU_DAY:
                value.tomorrow = true;
                break;
              case value.FRI_DAY:
                value.tomorrow = true;
                break;
              case value.SAT_DAY:
                value.tomorrow = true;
                break;
              case value.SUN_DAY:
                value.tomorrow = true;
                break;
              default:
                value.tomorrow = false;
            }
            if (value.tomorrow) $scope.tomorrowCount++;
          }

          value.repeats = "";
          value.repeats = (value.MON_DAY) ? weekShortNames[0] : "";
          value.repeats += (value.TUE_DAY) ? ((value.repeats) ? ", " + weekShortNames[1] : weekShortNames[1]) : "";
          value.repeats += (value.WED_DAY) ? ((value.repeats) ? ", " + weekShortNames[2] : weekShortNames[2]) : "";
          value.repeats += (value.THU_DAY) ? ((value.repeats) ? ", " + weekShortNames[3] : weekShortNames[3]) : "";
          value.repeats += (value.FRI_DAY) ? ((value.repeats) ? ", " + weekShortNames[4] : weekShortNames[4]) : "";
          value.repeats += (value.SAT_DAY) ? ((value.repeats) ? ", " + weekShortNames[5] : weekShortNames[5]) : "";
          value.repeats += (value.SUN_DAY) ? ((value.repeats) ? ", " + weekShortNames[6] : weekShortNames[6]) : "";
          value.detailDate = monthNames[todayDate.getMonth()] + " " + todayDate.getDate() + ", " + todayDate.getFullYear();

          if (value.CLASS_TIME && value.CLASS_TIME != ' - ') {
            var fromAndTo = value.CLASS_TIME.split("-");
            value.from = (fromAndTo.length > 0) ? fromAndTo[0].trim() : "";
            value.to = (fromAndTo.length > 1) ? fromAndTo[1].trim() : "";
            value.fromTime = new Date(todayDate.getFullYear() + "-" + (todayDate.getMonth() + 1) + "-" + todayDate.getDate() + " " + value.from);
            $scope.studentschedule.push(value);
          } else {
            if (value.courseActiveToday) {
              $scope.onlineTodayCount += 1;
              value.today = true
              $scope.todayCount += 1;

            }
            if (value.courseActiveTom) {
              $scope.onlineTomCount += 1;
              value.tomorrow = true;
              $scope.tomorrowCount += 1;
            }
            $scope.onlineclasses.push(value);
          }
        });
        console.log("student schedule:");
        console.log($scope.studentschedule);
        console.log("online schedule:");
        console.log($scope.onlineclasses);
      });
    }

