    $scope.loadgrouppage = function() {
      $rootScope.showlogin = false;
      var todayDate;
      try {
        var mDate1 = moment($rootScope.searchbydate).format();
        todayDate = new Date(mDate1);
      } catch (err) {
        console.error('problem with moment.js library');
        todayDate = new Date($rootScope.searchbydate);
      }
      var checkDay = todayDate.getDay() - 1
      if (checkDay < 0) {
        checkDay = 6
      }
      var todayWeekDayName = weekShortNames[checkDay];
      var todayMonthName = monthNames[todayDate.getMonth()];
      $scope.displayTodayCaption3 = todayWeekDayName + ", " + todayMonthName + " " + todayDate.getDate();
      var sdate;
      try {
        var mDate2 = moment($rootScope.searchbydate).format();
        sdate = new Date(mDate2);
      } catch (err) {
        console.error('problem with moment.js linrary');
        sdate = new Date($rootScope.searchbydate);
      }
      sdate.setHours(0, 0, 0, 0);

      $scope.scounter = 0;
      angular.forEach($scope.studentschedule, function(value, key) {
        let ClassStartDate = convertDate(value.START_DATE);
        let ClassEndDate = convertDate(value.END_DATE);
        console.log("start date-" + ClassStartDate + " - End Date - " + ClassEndDate);
        let currentDate = new Date();
        let dayIndex;
        let weekDay;
        // check if course is active for the date selected
        value.searchDateActive = false;
        if (sdate >= ClassStartDate && sdate <= ClassEndDate) {
          dayIndex = (sdate.getDay() == 0) ? 6 : sdate.getDay() - 1;
          weekDay = $scope.weekdays[dayIndex];
          switch (weekDay) {
            case value.MON_DAY:
              value.searchDateActive = true;
              break;
            case value.TUE_DAY:
              value.searchDateActive = true;
              break;
            case value.WED_DAY:
              value.searchDateActive = true;
              break;
            case value.THU_DAY:
              value.searchDateActive = true;
              break;
            case value.FRI_DAY:
              value.searchDateActive = true;
              break;
            case value.SAT_DAY:
              value.searchDateActive = true;
              break;
            case value.SUN_DAY:
              value.searchDateActive = true;
              break;
            default:
              value.searchDateActive = false;
          }
          $scope.scounter++;
        }
        value.detailDate = monthNames[sdate.getMonth()] + " " + sdate.getDate() + ", " + sdate.getFullYear();
      });

      $scope.existsdatewiseresults = false;
      angular.forEach($scope.onlineclasses, function(value, key) {
        value.dateSearchSuccess = false;
        var ClassStartDate = convertDate(value.START_DATE);
        var ClassEndDate = convertDate(value.END_DATE);

        if (sdate >= ClassStartDate && sdate <= ClassEndDate) {
          value.dateSearchSuccess = true;
          $scope.existsdatewiseresults = true;
          $scope.scounter++;
        }
      });

      $scope.$apply();
    }

    $scope.searchCoursesforDates = function() {
