  var middlewareUrl = pagedef.middlewareServerUrl;
  var licenseKey = pagedef.licenseKey;


  /* Declare page level variables  */
  var dropdownendpoint = "/bannertermswithaccountsummary";
  var listendpoint = "/banneraccountsummary/termCode=";

  $scope.listdata = [];
  $scope.selterm = "Select Term";
  $scope.indexTerm = $scope.indexTerm != undefined ? $scope.indexTerm : 0;

  $.blockUI();
  $rootScope.getAPI(config, dropdownendpoint, function(data) {
    $scope.dropdown = data.termswithaccountsummary;
    if ($scope.dropdown.length >= 1) {
		if(autoSelectTerm == "on"){
          $scope.termselect($scope.indexTerm);
		}
    } else {
      $scope.showsummary = false;
    }
  });



  $scope.termselect = function(index) {
    $.blockUI();
    $scope.listdata = [];
    var t = $scope.dropdown[index];
    $scope.selterm = t.DESCRIPTION;

    $('#clickName').blur();
    if (!$scope.selterm) {
      $scope.showsummary = false;
      $scope.listdata = [];
      return;
    }

    $rootScope.getAPI(config, listendpoint + $scope.dropdown[index].TERM_CODE, function(data) {
      $scope.listdata = data;
      if ($scope.listdata) {
        $scope.showsummary = true;
      } else {
        $scope.showsummary = false;
      }
    });
  }

}

