    $rootScope.feedlist = pagedef.feed;
    $scope.selTab = 1;
    $rootScope.showlogin = false;
    $rootScope.selTabAthletics = $rootScope.selTabAthletics != undefined ? $rootScope.selTabAthletics : 'sideArmTab1';
    $rootScope.indexAthletics = $rootScope.indexAthletics != undefined ? $rootScope.indexAthletics : 0;
    $scope.indexAthletics = $rootScope.indexAthletics != undefined ? $rootScope.indexAthletics : 0;
    var f = $rootScope.feedlist[$rootScope.indexAthletics];

    $rootScope.selSports = f.name;

    $scope.fetchSportData = function(index) {
      f = $rootScope.feedlist[index];
      $scope.indexAthletics = index;

      $rootScope.indexAthletics = index;

      var newsUrl = f.newsurl;
      $rootScope.selSports = f.name;
      $scope.selSportsName = $rootScope.selSports;

      $scope.loadnews();
      $scope.loadsch();
      $scope.loadsco();
    }

    $scope.goToDetailPage = function(index, type) {

      if (window.device) {
        var href = "app/AthleticsSidearms/AthleticsSidearmsPage2"
      } else {
        var href = "app/AthleticsSidearms/AthleticsSidearmsPage2"
      }

      $location.path(href).search({
        index: index,
        type: type
      });
    }

    $scope.getimage = function(content) {
      return $sce.trustAsHtml(content.split('"')[1]);
    }
    $scope.trustUrl = function(url) {
      return $sce.trustAsUrl(url);
    }
    $scope.trusthtml = function(data) {
      return $sce.trustAsHtml(data);
    }

    $scope.loadnews = function() {
      var newsUrl = f.newsurl;
      $rootScope.isblocking = true;
      var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
      var param = {
        url: newsUrl
      };
      $http.post(rssreaderUrl, param, '').then(function(data, status, headers, config) {
        $scope.newsfeeds = data.data.feed.entries;
        $rootScope.newsfeeds = data.data.feed.entries;
        $.unblockUI();
        $rootScope.isblocking = false;
      }, function(error, status, header, config) {
        console.log("Error Is", error);
        $.unblockUI();
      })
    }


    $scope.loadsch = function() {
      var currYear = (new Date().getFullYear() + '').substring(2);
      var prevYear = parseInt(new Date().getFullYear()) - 1;
      var url = f.schurl.replace("{{curr-next}}", prevYear + "-" + currYear);


      if (window.device) {} else {
        url = "/websimulator/xml?url=" + url;
      }

      var xhr = $.ajax({
        url: url,
        dataType: "xml",
        async: true,
        data: {},
        success: function(resp) {
          var results = $.xml2json(resp);
          $scope.schfeeds = results;
          $rootScope.schfeeds = results;
          $.unblockUI();
        },
        error: function(e) {
          $.unblockUI();
        }
      });
    }



    $scope.loadsco = function() {
      $.blockUI();
      var url = f.scourl;
      var sportsAbbr = f.scotag;
      var scoreArray = [];

      if (window.device) {} else {
        url = "/websimulator/xml?url=" + f.schurl;
      }

      // var xhr = $.ajax({
      //   url: url,
      //   dataType: "json",
      //   async: true,
      //   data: {},
      //   success: function(resp) {
      //     var scoreArray = [];

      //     for (var i = 0; i < resp.scores.length; i++) {
      //       if (resp.scores[i].sport_abbrev == sportsAbbr) {
      //         scoreArray.push(resp.scores[i]);
      //       }
      //     }
      //     $scope.scofeeds = scoreArray;
      //     $rootScope.scofeeds = scoreArray;
      //     $scope.$apply();
      //     //$scope.$digest();
      //     $.unblockUI();

      //   },
      //   error: function(e) {
      //     // navigator.notification.alert("Schedule could not be loaded at this time. Please try again later");
      //     $.unblockUI();

      //   }
      // });

      var xhr = $.ajax({
        url: url,
        dataType: "xml",
        async: true,
        data: {},
        success: function(resp) {
          setTimeout(() => {
            $scope.$apply()
          }, 500);

          var results = $.xml2json(resp);
          var feedResults = results.channel.item;
          var now = new Date();
          var filteredResult = [];

          $.each(feedResults, function(key, val) {
            var feedDate = new Date(val.startdate);

            if (feedDate < now) {
              var scoreRegEx = /\\n(.*?)\\n/
                //var scoreRegEx = /\\n(.*)\\nStreaming Video:/
                //var scoreNextRegEx = /\\n(.*)\\n/

              var score = val.description.match(scoreRegEx) ? val.description.match(scoreRegEx)[1].substring(2) : null

              if (score) {
                val.displayScore = score
                filteredResult.push(val)
              }
            }

          });

          $scope.scofeeds = filteredResult;
          $rootScope.scofeeds = filteredResult;

          $.unblockUI();
        },
        error: function(e) {
          $.unblockUI();
        }
      });
