      var dateOut = new Date(stringDate);
      var hr = dateOut.getUTCHours();
      var mint = dateOut.getUTCMinutes();
      if (hr == 4 && mint == 0) {
        dateOut.setDate(dateOut.getDate());
        var dateString = dateOut.toString().substring(0, 16);
        var dateTimeString = dateString + "  TBA";
        return dateTimeString;
      } else {
        dateOut.setDate(dateOut.getDate());
        return dateOut;
      }
    }

    $scope.convertToDateNews = function(stringDate) {
      var dateOut = new Date(stringDate);
      dateOut.setDate(dateOut.getDate());
      return dateOut;
    }

    $scope.filterTitle = function(title) {
      var filText = title.replace("Jefferson Community College vs.", "").replace("vs. Jefferson Community College", "");
      return filText;
    }

    $scope.selectTab = function(tab) {
      $("#sideArmTab1").removeClass("toolbar-column-active");
      $("#sideArmTab2").removeClass("toolbar-column-active");
      $("#sideArmTab3").removeClass("toolbar-column-active");
      $("#" + tab).addClass("toolbar-column-active");
      if (tab == "sideArmTab1") {
        $scope.selTab = 1;
        $rootScope.selTabAthletics = 'sideArmTab1';
      } else if (tab == "sideArmTab2") {
        $scope.selTab = 2;
        $rootScope.selTabAthletics = 'sideArmTab2';
      } else if (tab == "sideArmTab3") {
        $scope.selTab = 3;
        $rootScope.selTabAthletics = 'sideArmTab1';
      }
    }
    $scope.selectTab($rootScope.selTabAthletics);
  } catch (e) {
    console.log(e)
  }
}

