    var dateOut = new Date(stringDate);
    dateOut.setDate(dateOut.getDate());
    var hr = dateOut.getUTCHours();
    var mint = dateOut.getUTCMinutes();
    if (hr == 0 && mint == 0) {
      var dateString = dateOut.toString().substring(0, 16);
      var dateTimeString = dateString + "  TBA";
      return dateTimeString;
    } else {
      return dateOut;
    }
