  function parseDateTime(dt) {
    var date = false;
    if (dt) {
      var c_date = new Date(dt);
      var hrs = c_date.getHours();
      var min = c_date.getMinutes();
      if (isNaN(hrs) || isNaN(min) || c_date === "Invalid Date") {
        return null;
      }
      var type = (hrs <= 12) ? "AM" : "PM";
      date = ((+hrs % 12) || hrs) + ":" + min + type;
    }
    return date;
  }

