  function getDateTimeOnTenantTimeZone(dt) {
    console.log("UNformatted date: " + dt);
    if (!dt) return null;
    let dtFormatted;
    let timeZoneVal;
    try {
      if (appletOverridedTimeZone && appletOverridedTimeZone != "null" && appletOverridedTimeZone.trim() != '') {
        timeZoneVal = appletOverridedTimeZone;
      } else {
        timeZoneVal = ($rootScope.user && $rootScope.user.timeZone) ? $rootScope.user.timeZone : '';
      }
      if (!timeZoneVal) {
        console.warn("Timezone is not configured for tenant, date values to be displayed in local timezone !");
      } else {
        dtFormatted = moment(dt).tz(timeZoneVal).format('DD MMM, YYYY, hh:mmA');
        console.log("Formatted date: " + dtFormatted);
        if (dtFormatted) {
          return dtFormatted;
        } else {
          console.error("Formatted date value is blank, some issue with configuration. Date values to be displayed in local timezone !");
        }
      }
    } catch (ex) {
      console.error("Moment library not supported, date values to be displayed in local timezone !");
    }
    var sentDate = new Date(dt);
    var timein12H = parseDateTime(sentDate).replace(":00", "").replace(":0", "");
    dtFormatted = sentDate.getDate() + " " + MONTHS[sentDate.getMonth()] + ", " + sentDate.getFullYear() + ", " + timein12H;
    console.log("**Formatted date: " + dtFormatted);
    return dtFormatted;
  }

  $scope.loadPage = function(page) {
    page++;

    // console.log($scope.selChannels);
    //  console.log($scope.selStatus);
    var channelStr = $scope.selChannels.toString();
    var status = "";
    if ($scope.selStatus && $scope.selStatus.length == 1) {
      status = ($scope.selStatus.indexOf("Sent") == -1) ? "Scheduled" : "Sent";
    }
    channelStr = (channelStr) ? "&channels=" + channelStr : "";
    status = (status) ? "&status=" + status : "";
    if (channelStr || status) {
      $scope.searchCase = 1;
    }

    var url = `/unifydmessaging/feed/search?user=${$rootScope.user.email}&sort=createdDate,DESC&size=5&page=` + page + channelStr + status;
    $.blockUI();
    $rootScope.callAPI(url, 'GET', {}, function(res) {
      if (res != undefined && res) {
        $scope.page = res.data.number;
        $scope.last = res.data.last;


        //  console.log("1",$scope.setMess);
        // console.log("2",$scope.messagingList);
        if (!$scope.messagingList) {
          $scope.messagingList = res.data.content;
          // console.log("3",$scope.setMess);
          //  console.log("4",$scope.messagingList);
          if ($scope.messagingList.length > 0 && $scope.messagingList != undefined) {
            //    console.log("5",$scope.setMess);
            //console.log("6",$scope.messagingList);
            $.unblockUI();
            $scope.messagingList = $scope.messagingList.map(function(value, key) {
              value.shortMessage = value.defaultMessage.slice(0, 240);
              value.shortMessage = value.shortMessage.replace(/<br\/>/g, '\n');
              value.messageDetailLess = 0;
              if (!value.sent) {
                var today = new Date();
                var scheduleTime = new Date(value.scheduleTime);
                var timein12H, formattedDateStr;
                if (today > scheduleTime) {
                  if (value.createdDate == value.updatedDate) {
                    formattedDateStr = getDateTimeOnTenantTimeZone(value.createdDate);
                  } else {
                    formattedDateStr = getDateTimeOnTenantTimeZone(value.updatedDate);
                  }
                  formattedDateStr = "Sent - " + formattedDateStr;
                  value.messageStatus = "Sent";
                  value.messageTitle = formattedDateStr;
                } else {
                  formattedDateStr = "Scheduled - " + getDateTimeOnTenantTimeZone(value.scheduleTime);
                  value.messageStatus = "Scheduled";
                  value.messageTitle = formattedDateStr;
                }
              } else {
                formattedDateStr = "Sent - " + getDateTimeOnTenantTimeZone(value.createdDate);
                value.messageStatus = "Sent";
                value.messageTitle = formattedDateStr;
              }
              return value;
            });
          } else {
            $scope.messagingList = [];
            $scope.setMess = 1;
            //   console.log("newtest", $scope.setMess);
            //  console.log("newtest1", $scope.messagingList);
            $.unblockUI();
          }
        } else {
          // console.log("loding................");
          $.unblockUI();
          $scope.messagingList = $scope.messagingList.concat(res.data.content);
          if ($scope.messagingList.length > 0) {
