                if (today > scheduleTime) {
                  formattedDateStr = "Sent - " + getDateTimeOnTenantTimeZone(value.createdDate);
                  value.messageStatus = "Sent";
                  value.messageTitle = formattedDateStr;
                } else {
                  formattedDateStr = "Scheduled - " + getDateTimeOnTenantTimeZone(value.scheduleTime);
                  value.messageStatus = "Scheduled";
                  value.messageTitle = formattedDateStr;
                }
              } else {
