      $location.path('/app/MessageModule/MessageModulePage2');
    } catch (e) {
      alert(e);
    }
  }

  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, $scope.title, null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: $scope.title,
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns focusTranspatent',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }
    }
  }
  setTimeout(function() {
    $('.dropdown').keyup(function(e) {
      if (e.keyCode == 40) {
        $(this).addClass('open')
      } else if (e.keyCode == 38) {
        $(this).removeClass('open')
      }
    });
    $(".messagingList  a").on('click', function(e) {
      var url = $(this).attr('href');
      var target = $(this).attr('target');

      if (target == 'blank') {
        e.preventDefault();
        e.stopPropagation();
        var iabRef;

        if (window.device) {
          $.blockUI();
          iabRef = window.open(url, "_blank", "location=yes,hidden=yes,enableViewportScale=yes,clearcache=yes,closebuttoncaption=close");
        } else {
          iabRef = window.open(url, "_blank");
          //window.history.back();
        }

        var hideBlockUi = function() {
          $.unblockUI();
          iabRef.show();
        };

        var iabClose = function(data) {
          $.unblockUI();

          window.StatusBar.backgroundColorByHexString($rootScope.statusBarColor ? $rootScope.statusBarColor : '#000000');
          iabRef.removeEventListener("exit", iabClose);
          window.screen.orientation.lock('portrait')
            //window.history.back();
        };

        iabRef.addEventListener("exit", iabClose);

        var loadStop = function(data) {
          hideBlockUi();
          iabRef.removeEventListener("loadstop", loadStop);
        };
        iabRef.addEventListener("loadstop", loadStop);

        //window.open(url, '_blank');
      }
    });
  }, 1000);
}

