          return;
        }

        if ($rootScope.selectedChannelsMessaging.indexOf('connect') !== -1 && $scope.connectTags.indexOf('all') !== -1) {

          var category = [];
          for (var i = 1; i < $scope.categoryMessaging.length; i++) {
            if ($scope.categoryMessaging[i].enable) category.push($scope.categoryMessaging[i].id)
          }
          $scope.connectTags = category;
        }


        if ($scope.editMode) {
          $scope.showAlert('Please save your changes.');
          return;
        }

        var messageBody = {
          external: $scope.external,
          groups: $rootScope.selectedGroupMessaging,
          individualUser: $rootScope.selectedUsersMessaging,
          gender: [], // $rootScope.selectedGenderMessaging,
          title: $scope.title,
          defaultMessage: $scope.description,
          channels: $rootScope.selectedChannelsMessaging,
          scheduledMsg: !$scope.publishNow,
          important: $scope.priority === 'High',
          reqAck: $scope.requireAck,
          override: $scope.overrideUser,
          tags: $scope.connectTags || [],
          sent: true,
          redirectUrl: $scope.redirectionUrl
        };


        if (!$scope.publishNow) {
          var tDate = new Date();
          var currentDate = new Date(tDate.getFullYear(), tDate.getMonth(), tDate.getDate(), tDate.getHours(), tDate.getMinutes(), 0);
          //var scheduleDate = new Date($('#datetimepicker11')[0].value);
          var scheduleDate;
          if ($rootScope.webApp) {
            scheduleDate = new Date($('#datetimepicker11')[0].value);
          } else {
            scheduleDate = new Date($('#datetimepicker11').text());
          }

          if (scheduleDate.getTime() < currentDate.getTime()) {
            $scope.showAlert('Scheduled time should be greater that current time.');
            return
          }

          if (scheduleDate.getTime() - currentDate.getTime() < 1800000) {
            //console.log(scheduleDate.getTime());
            //console.log("H " + scheduleDate.getHours() + " M " + scheduleDate.getMinutes() + " S" + scheduleDate.getSeconds());
            //console.log(currentDate.getTime());
            //console.log("H " + currentDate.getHours() + " M " + currentDate.getMinutes() + " S" + currentDate.getSeconds());
            $scope.showAlert('Scheduled time should be at least 30 minutes.');
            return
          }
          messageBody['scheduleTime'] = scheduleDate;
        }

        /*** flag set for scheduleMsg***/
        if (messageBody.scheduledMsg) {
          messageBody.sent = false;
        } else {
          messageBody.sent = true;
        }

        var channelMsg = {};
        if ($rootScope.selectedChannelsMessaging.indexOf('connect') !== -1) {
          /* channelMsg['whatsUp'] = $('#connectMessage').val(function(i, v) { //index, current value
           return v.replace(/(\r\n|\n)/g, "<br/>");
           }); */

          channelMsg['whatsUp'] = $('#connectMessage').val().replace(/(\r\n|\n)/g, "<br/>");
          messageBody.defaultMessage = $rootScope.selectedChannelsMessaging.length > 1 ? $scope.description : channelMsg['whatsUp'];
        }
        if ($rootScope.selectedChannelsMessaging.indexOf('sms') !== -1) {
          channelMsg['sms'] = $('#smsMessage').val();
          messageBody.defaultMessage = $rootScope.selectedChannelsMessaging.length > 1 ? $scope.description : channelMsg['sms'];
        }
        if ($rootScope.selectedChannelsMessaging.indexOf('email') !== -1) {
          channelMsg['email'] = $('#emailMessage').val();
          messageBody.defaultMessage = $rootScope.selectedChannelsMessaging.length > 1 ? $scope.description : channelMsg['email'];
          channelMsg['email'] = channelMsg['email'].replace(/(?:\r\n|\r|\n)/g, '<br/>');
        }
        if ($rootScope.selectedChannelsMessaging.indexOf('notification') !== -1) {
          channelMsg['notification'] = $('#notificationMessage').val();
          messageBody.defaultMessage = $rootScope.selectedChannelsMessaging.length > 1 ? $scope.description : channelMsg['notification'];
        }


        messageBody['channelMsg'] = channelMsg;

        if ($scope.imageSource && $scope.imageSource === 'camera') {
          $.blockUI();
          var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/imageUpload";
          var data = {
            'imageData': $scope.postImage,
            "imageExtn": 'png'
          };
          if ($scope.editSchduleMessageImage) {
            messageBody['graphicType'] = 'image';
            messageBody['graphicUrl'] = $scope.postImage;
            messageBody['graphicName'] = $scope.postContentTitle;
            $scope.savePost(messageBody);
          } else {
            $http.post(url, data).then(function(resp) {
              if (resp.data.url) {
                messageBody['graphicType'] = 'image';
                messageBody['graphicUrl'] = resp.data.url;
                messageBody['graphicName'] = $scope.postContentTitle;
                $scope.savePost(messageBody);
              } else {
                $.unblockUI();
              }
            });
          }
        } else if ($scope.imageSource && $scope.imageSource === 'youtube') {
          messageBody['graphicType'] = 'video';
          messageBody['graphicUrl'] = $scope.videoId;
          messageBody['graphicName'] = $scope.postContentTitle;
          $scope.savePost(messageBody);
        } else {
          messageBody['graphicType'] = '';
          messageBody['graphicUrl'] = '';
          messageBody['graphicName'] = "";
          $scope.savePost(messageBody);
        }
      }
    };

