      label: "Wed",
      selected: ""
    }, {
      style: "",
      label: "Thu",
      selected: ""
    }, {
      style: "",
      label: "Fri",
      selected: ""
    }, {
      style: "",
      label: "Sat",
      selected: ""
    }, {
      style: "",
      label: "Sun",
      selected: ""
    }];
