      $scope.showaltpin = false;
      $scope.fetchAdditionalServices();
    }
    */
    $scope.showaltpin = false;
    $scope.fetchAdditionalServices();
  }

  $scope.fetchAdditionalServices = function() {
    var url = (demoAPI) ? "/bannergetRegCartOtherLOV" : subjectDropdownListUrl + $scope.selectedtermCode;
    $rootScope.getAPI(config, url, function(data) {
      //$scope.listdata = data;
      $scope.subjectList = data;
    });
  }

  $scope.subjectselect = function(subject) {
    $scope.selectedsubject = subject.SUBJECT_DESC;
    $scope.selectedsubjectCode = subject.SUBJECT_CODE;
  }

  $scope.toggleAdvancedSearch = function() {
    if (!$scope.toggleSearch) {
      if ($scope.listdata.length == 0) {
        var url = (demoAPI) ? "/bannergetRegCartOtherLOV" : dropdownListUrl + $scope.selectedtermCode;
        $rootScope.getAPI(config, url, function(data) {
          $scope.listdata = data;
          $scope.toggleSearch = true;
        });
      }
    }
  }

  $scope.campusselect = function(campus) {
    $scope.selectedcampus = campus.CAMP_DESC;
    $scope.selectedcampusCode = campus.CAMP_CODE;
  }

  $scope.courseselect = function(course) {
    $scope.selectedcourse = course.TITLE;
    $scope.selectedcourseCode = course.TITLE;
  }

  $scope.instructorselect = function(inst) {
    $scope.selectedInstructor = inst.SPRIDEN_FIRST_NAME + ", " + inst.SPRIDEN_LAST_NAME;
    $scope.selectedInstructorCode = inst.SPRIDEN_PIDM;
  }

  $scope.selectDay = function(day) {
    if ($scope.weekdata[day].style == "active") {
      $scope.weekdata[day].style = "";
      $scope.weekdata[day].selected = "";
    } else {
      $scope.weekdata[day].style = "active";
      $scope.weekdata[day].selected = "Y";
    }
  }

  function inCart(crn) {
    var retVal = false;
    angular.forEach($scope.cart, function(value, key) {
      if (value.CRN == crn) {
        retVal = true;
        return;
      }
    });
    return retVal;
  }

  $scope.showResults = function() {
    if (!$scope.selectedtermCode) {
      showAlert("Select a term.");
      return;
    }
    if (!$scope.selectedsubjectCode && !$scope.selectedInstructorCode) {
      showAlert("Select a subject or instructor.");
      return;
    }
    $scope.showmessage = false;
    var mon, tue, wed, thu, fri, sat, sunday;
    mon = ($scope.weekdata[0].selected == 'Y') ? 'M' : '';
    tue = ($scope.weekdata[1].selected == 'Y') ? 'T' : '';
    wed = ($scope.weekdata[2].selected == 'Y') ? 'W' : '';
    thu = ($scope.weekdata[3].selected == 'Y') ? 'R' : '';
    fri = ($scope.weekdata[4].selected == 'Y') ? 'F' : '';
    sat = ($scope.weekdata[5].selected == 'Y') ? 'S' : '';
    sunday = ($scope.weekdata[6].selected == 'Y') ? 'U' : '';
    var startDate = "",
      endDate = "";
    var open_course = ($scope.showOpenCourse) ? 'Y' : 'N';
    var vday, vmonth, vyear;
    if ($('#startDate')[0].value) {
      var dt = new Date($('#startDate')[0].value);
      vday = dt.getDate();
      vday = (vday.toString().length == 1) ? "0" + vday : vday;
      vmonth = monthName[dt.getMonth()];
      vyear = dt.getFullYear();
      startDate = vday + "-" + vmonth + "-" + vyear;
    }
    if ($('#endDate')[0].value) {
      var dt = new Date($('#endDate')[0].value);
      vday = dt.getDate();
      vday = (vday.toString().length == 1) ? "0" + vday : vday;
      vmonth = monthName[dt.getMonth()];
      vyear = dt.getFullYear();
      endDate = vday + "-" + vmonth + "-" + vyear;
    }
    var searchURL = classSearchEndpoint + "/termCode=" + $scope.selectedtermCode +
      "&subjectCode=" + $scope.selectedsubjectCode +
      "&courseNumber=" + $scope.courseNumber +
      "&partTermCode=" + $scope.partTermCode +
      "&facultyPidm=" + $scope.selectedInstructorCode +
      "&courseTitle=" + $scope.selectedcourseCode +
      "&creditFrom=" + $scope.creditFrom +
      "&creditTo=" + $scope.creditTo +
      "&campCode=" + $scope.selectedcampusCode +
      "&opencourse=" + open_course +
      "&startDate=" + startDate +
      "&endDate=" + endDate +
      "&mon=" + mon +
      "&tue=" + tue +
      "&wed=" + wed +
      "&thu=" + thu +
      "&fri=" + fri +
      "&sat=" + sat +
      "&sun=" + sunday;
    searchURL = (demoAPI) ? "/bannerregCartclasslookup" : searchURL;
    $rootScope.getAPI(config, searchURL, function(data) {
      if (data.CLASSLOOKUP.length == 0) {
        showAlert("No classes found.");
      } else {
        console.log(data.CLASSLOOKUP);
        $scope.classes = [];
        $scope.inSearchWindow = false;
        var prevValue = "";
        var vindex = 0;
        angular.forEach(data.CLASSLOOKUP, function(value, key) {
          if (value[0].TIME && value[0].TIME.length > 8) {
            value[0].TIME_DISPLAY = value[0].SSRMEET_START_TIME + " - " + value[0].SSRMEET_END_TIME;
          } else {
            value[0].TIME_DISPLAY = "TBA";
          }

          value[0].START_DATE = "";
          value[0].END_DATE = "";
          value[0].ONLINE_COURSE = false;
          if ((value[0].SSRMEET_START_TIME == '' || value[0].SSRMEET_START_TIME == null) &&
            (value[0].SSRMEET_END_TIME == '' || value[0].SSRMEET_END_TIME == null) &&
            (value[0].TIME == "TBA")) {
            value[0].ONLINE_COURSE = true;
          }
          var days = value[0].TIME.indexOf(" ") != -1 ? value[0].TIME.split(" ")[0] : "";
          value[0].WEEK_DATA = "";
          value[0].WEEK_DATA += (days.indexOf("M") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Mon" : "";
          value[0].WEEK_DATA += (days.indexOf("T") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Tue" : "";
          value[0].WEEK_DATA += (days.indexOf("W") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Wed" : "";
          value[0].WEEK_DATA += (days.indexOf("R") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Thu" : "";
          value[0].WEEK_DATA += (days.indexOf("F") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Fri" : "";
          value[0].WEEK_DATA += (days.indexOf("S") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Sat" : "";
          value[0].WEEK_DATA += (days.indexOf("U") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Sun" : "";
          //if (value[0].WEEK_DATA) value[0].WEEK_DATA = value[0].WEEK_DATA.substring(0, value[0].WEEK_DATA.length - 1);

          if (!value[0].schedule) {
            value[0].schedule = [];
          }
          value[0].schedule.push({
            "DURATION": value[0].DURATION,
            "INSTRUCTOR": value[0].INSTRUCTOR,
            "TIME_DISPLAY": value[0].TIME_DISPLAY,
            "TIME": value[0].TIME,
            "LOCATION": value[0].LOCATION,
            "WEEK_DATA": value[0].WEEK_DATA
          });
          value[0].addedToCart = inCart(value[0].CRN);
          value[0].formatClass = "";
          value[0].REGISTERED = false;
          angular.forEach($scope.currentSchedule, function(schvalue, schkey) {
            if (schvalue.SFTREGS_CRN == value[0].CRN) {
              value[0].REGISTERED = true;
              //return;
            }
          });
          //
          if (prevValue == "") {
            $scope.classes.push(value[0]);
            prevValue = value[0];
          } else if (prevValue.CRN == value[0].CRN &&
            //prevValue.TERM_CODE == value[0].TERM_CODE &&
            //prevValue.SUBJECT_CODE == value[0].SUBJECT_CODE &&
            prevValue.CREDIT_HOURS == value[0].CREDIT_HOURS &&
            prevValue.COURSE_ID == value[0].COURSE_ID) {
            $scope.classes[vindex].schedule.push(value[0].schedule[0]);
          } else {
            vindex++;
            $scope.classes.push(value[0]);
            prevValue = value[0];
          }

        });
        $scope.page = 2;
        //$rootScope.appletName = "Courses";
      }
    });

  }

  $scope.resetSearch = function() {
    $scope.selectedsubject = "Subject";
    $scope.selectedsubjectCode = "";
    $scope.selectedcourse = "Course Title";
    $scope.selectedcampus = "Campus";
    $scope.selectedInstructor = "Instructor";
    $scope.selectedcourseCode = "";
    $scope.selectedcampusCode = "";
    $scope.selectedInstructorCode = "";
    angular.forEach($scope.weekdata, function(value, key) {
      value.style = "";
      value.selected = "";
    })
    $('#startDate').val("");
    $('#endDate').val("");
    $scope.showOpenCourse = false;
  }

  $scope.addToCart = function(crn) {
    angular.forEach($scope.classes, function(value, key) {
      if (value.CRN == crn) {
        if (value.REMAINING <= 0) {
          return;
        }
        if (value.OLR_COURSE == 'Y') {
          if (!value.START_DATE && !value.END_DATE) {
            showAlert("Start Date or End Date should be mentioned for OLR courses.");
            return;
          }
          if (value.START_DATE && value.END_DATE) {
            showAlert("Only one of start or end dates may be entered.");
            return;
          }
          var cStartDate, cEndDate;
          if (value.START_DATE) {
            var dStartDate = new Date(value.START_DATE);
            cStartDate = dStartDate.getDate() + "-" + monthName[dStartDate.getMonth()] + "-" + dStartDate.getFullYear();
          }
          if (value.END_DATE) {
            var dEndDate = new Date(value.END_DATE);
            cEndDate = dEndDate.getDate() + "-" + monthName[dEndDate.getMonth()] + "-" + dEndDate.getFullYear();
          }
        }

        value.addedToCart = true;
        value.formatClass = "in-cart-course";
        $scope.cart.push(value);
        console.log($scope.cart);
        return;
      }
    });

  }

  $scope.removeFromCart = function(crn) {
    for (var i = 0; i <= $scope.cart.length - 1; i++) {
      if ($scope.cart[i].CRN === crn) {
        $scope.cart.splice(i, 1);
      }
    }
    angular.forEach($scope.classes, function(value, key) {
      if (value.CRN == crn) {
        value.addedToCart = false;
        value.formatClass = "";
        console.log($scope.cart);
      }
    });
  }

  $scope.gotoBack = function() {
    $scope.page = 1;
    //$rootScope.appletName = "Lookup Courses";
  }

  $scope.gotoCart = function() {
    $scope.page = 3;
    //$rootScope.appletName = "Cart";
  }

  $scope.gotoResultsPage = function() {
    $scope.page = 2;
    //$rootScope.appletName = "Courses";
  }

  $scope.submitCart = function() {
    if ($scope.cart.length == 0) {
      showAlert("No classes added to cart.");
      return;
    }
    //build param map for registration service
    var endpoint = "/banneraddDropClasses";
    var postdata = "";
    var termCode = "";
    var crns = "";
    var regCodes = "";
    var action = "A";
    var startDates = "";
    var endDates = "";
    var firstTime = true;
    $scope.regResults = "";

    angular.forEach($scope.cart, function(value, key) {
      termCode = $scope.selectedtermCode;
      if (!firstTime) {
        crns = crns + ",";
        regCodes = regCodes + ",";
        startDates = startDates + ",";
        endDates = endDates + ",";
      }
      crns = crns + value.CRN;
      regCodes = regCodes + "RW";
      if (value.START_DATE) {
        var dStartDate = new Date(value.START_DATE);
        cStartDate = (dStartDate.getDate() + 1) + "-" + monthName[dStartDate.getMonth()] + "-" + dStartDate.getFullYear();
        startDates = startDates + cStartDate;
      }
      if (value.END_DATE) {
        var dEndDate = new Date(value.END_DATE);
        cEndDate = (dEndDate.getDate() + 1) + "-" + monthName[dEndDate.getMonth()] + "-" + dEndDate.getFullYear();
        endDates = endDates + cEndDate;
      }
      firstTime = false;
    });
    postdata = "termCode=" + termCode + "&crns=" + crns + "&regCodes=" + regCodes + "&action=" + action + "&startDates=" + startDates + "&endDates=" + endDates;
    console.log(postdata);
    $rootScope.postAPI(config, endpoint, postdata, function(data) {
      if (typeof data.p_out[0].summary == "string") {
        var dres = JSON.parse(data.p_out[0].summary);
        data.p_out[0].summary = dres;
      }
      $scope.regResults = data.p_out[0];
      if ($scope.regResults.summary && $scope.regResults.summary.results) {
        angular.forEach($scope.regResults.summary.results, function(value, key) {
          if (value.status == 'S') {
            value.CSS_CLASS = "alert-success";
          } else if (value.status == 'E') {
            value.CSS_CLASS = "alert-danger";
          } else {
            value.CSS_CLASS = "alert-warning";
          }
        });
      }
      $scope.showmessage = true;
      $scope.clearCart();
      $scope.page = 4;
    });
  }

  $scope.gotoHomePage = function() {
    $scope.regResults = "";
    $scope.page = 1;
    //$rootScope.appletName = "Lookup Courses";
  }

  function initModule() {
    initScopeVars();
    initWeekData();
    //$rootScope.appletName = "Lookup Courses";
    $rootScope.getAPI(config, termdropdownURL, function(data) {
      $scope.termdropdown = data.termdropdown;
      $scope.clearCart();
      if (angular.isArray($scope.termdropdown) && $scope.termdropdown.length > 0) {
        
        if (autoSelectTerm == "on") {
         $scope.selectedtermCode = $scope.termdropdown[0].TERM_CODE;
         $scope.selectedterm = $scope.termdropdown[0].TERM_DESC;
         $scope.dropdownselect($scope.termdropdown[0]);
        }
      }
    });
  }

  initModule();

  /**
   * UI related tasks
   */
  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, $scope.title, null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: $scope.title,
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }
    }
  }

}

