            if ($scope.address.primaryphone.PHONE_NUMBER && $scope.address.primaryphone.PHONE_NUMBER.search(/^[0-9 ]+$/) === -1) {
                $scope.alert("Phone number cannot be alphanumeric.")
                return false;
            }
            if ($scope.address.primaryphone.EXTENSION && $scope.address.primaryphone.EXTENSION.search(/^[0-9 ]+$/) === -1) {
                $scope.alert("Phone extension cannot be alphanumeric.")
                return false;
            }
        }
        var haveError = false;
        angular.forEach($scope.address.additionalphones, function(value, key) {
            if (value.PHONE_CODE == '' || value.PHONE_CODE == null) {
                haveError = true;
                $scope.alert("Telephone Type is required.", "Address");
                return false;
            } else if (value.PHONE_NUMBER == '' || value.PHONE_NUMBER == null) {
                haveError = true;
                $scope.alert("Telephone Number is required.", "Address");
                return false;
            }
            if (value.AREA_CODE && value.AREA_CODE.search(/^[0-9 ]+$/) === -1) {
                haveError = true;
                $scope.alert("Areacode cannot be alphanumeric.", "Address")
                return false;
            }
            if (value.PHONE_NUMBER && value.PHONE_NUMBER.search(/^[0-9 ]+$/) === -1) {
                haveError = true;
                $scope.alert("Phone number cannot be alphanumeric.", "Address")
                return false;
            }
            if (value.EXTENSION && value.EXTENSION.search(/^[0-9 ]+$/) === -1) {
                haveError = true;
                $scope.alert("Phone extension cannot be alphanumeric.", "Address")
                return false;
            }
        });
        if (haveError) {
